INSERT INTO `wp_3_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2016-04-28 16:25:09', '2016-04-28 16:25:09', 'Bienvenido a <a href="http://localhost/ciudadfutura/">Sitios ciudadfutura</a>. Este es tu primera entrada. ¡Edítala o elimínala, y empieza a bloguear!', '¡Hola mundo!', '', 'publish', 'open', 'open', '', 'hola-mundo', '', '', '2016-04-28 16:25:09', '2016-04-28 16:25:09', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?p=1', 0, 'post', '', 1),
(6, 1, '2016-04-28 19:16:52', '2016-04-28 19:16:52', '', 'Activity', '', 'publish', 'closed', 'closed', '', 'activity', '', '', '2016-04-28 19:16:52', '2016-04-28 19:16:52', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/activity/', 0, 'page', '', 0),
(7, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 'Members', '', 'publish', 'closed', 'closed', '', 'members', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/members/', 0, 'page', '', 0),
(8, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', '{{poster.name}} replied to one of your updates:\n\n<blockquote>&quot;{{usermessage}}&quot;</blockquote>\n\n<a href="{{{thread.url}}}">Go to the discussion</a> to reply or catch up on the conversation.', '[{{{site.name}}}] {{poster.name}} replied to one of your updates', '{{poster.name}} replied to one of your updates:\n\n"{{usermessage}}"\n\nGo to the discussion to reply or catch up on the conversation: {{{thread.url}}}', 'publish', 'closed', 'closed', '', 'site-name-poster-name-replied-to-one-of-your-updates', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=8', 0, 'bp-email', '', 0),
(9, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', '{{poster.name}} replied to one of your comments:\n\n<blockquote>&quot;{{usermessage}}&quot;</blockquote>\n\n<a href="{{{thread.url}}}">Go to the discussion</a> to reply or catch up on the conversation.', '[{{{site.name}}}] {{poster.name}} replied to one of your comments', '{{poster.name}} replied to one of your comments:\n\n"{{usermessage}}"\n\nGo to the discussion to reply or catch up on the conversation: {{{thread.url}}}', 'publish', 'closed', 'closed', '', 'site-name-poster-name-replied-to-one-of-your-comments', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=9', 0, 'bp-email', '', 0),
(10, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', '{{poster.name}} mentioned you in a status update:\n\n<blockquote>&quot;{{usermessage}}&quot;</blockquote>\n\n<a href="{{{mentioned.url}}}">Go to the discussion</a> to reply or catch up on the conversation.', '[{{{site.name}}}] {{poster.name}} mentioned you in a status update', '{{poster.name}} mentioned you in a status update:\n\n"{{usermessage}}"\n\nGo to the discussion to reply or catch up on the conversation: {{{mentioned.url}}}', 'publish', 'closed', 'closed', '', 'site-name-poster-name-mentioned-you-in-a-status-update', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=10', 0, 'bp-email', '', 0),
(11, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', '{{poster.name}} mentioned you in the group "{{group.name}}":\n\n<blockquote>&quot;{{usermessage}}&quot;</blockquote>\n\n<a href="{{{mentioned.url}}}">Go to the discussion</a> to reply or catch up on the conversation.', '[{{{site.name}}}] {{poster.name}} mentioned you in an update', '{{poster.name}} mentioned you in the group "{{group.name}}":\n\n"{{usermessage}}"\n\nGo to the discussion to reply or catch up on the conversation: {{{mentioned.url}}}', 'publish', 'closed', 'closed', '', 'site-name-poster-name-mentioned-you-in-an-update', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=11', 0, 'bp-email', '', 0),
(12, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', 'Thanks for registering!\n\nTo complete the activation of your account, go to the following link: <a href="{{{activate.url}}}">{{{activate.url}}}</a>', '[{{{site.name}}}] Activate your account', 'Thanks for registering!\n\nTo complete the activation of your account, go to the following link: {{{activate.url}}}', 'publish', 'closed', 'closed', '', 'site-name-activate-your-account', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=12', 0, 'bp-email', '', 0),
(13, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', 'Thanks for registering!\n\nTo complete the activation of your account and site, go to the following link: <a href="{{{activate-site.url}}}">{{{activate-site.url}}}</a>.\n\nAfter you activate, you can visit your site at <a href="{{{user-site.url}}}">{{{user-site.url}}}</a>.', '[{{{site.name}}}] Activate {{{user-site.url}}}', 'Thanks for registering!\n\nTo complete the activation of your account and site, go to the following link: {{{activate-site.url}}}\n\nAfter you activate, you can visit your site at {{{user-site.url}}}.', 'publish', 'closed', 'closed', '', 'site-name-activate-user-site-url', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=13', 0, 'bp-email', '', 0),
(14, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', '<a href="{{{initiator.url}}}">{{initiator.name}}</a> wants to add you as a friend.\n\nTo accept this request and manage all of your pending requests, visit: <a href="{{{friend-requests.url}}}">{{{friend-requests.url}}}</a>', '[{{{site.name}}}] New friendship request from {{initiator.name}}', '{{initiator.name}} wants to add you as a friend.\n\nTo accept this request and manage all of your pending requests, visit: {{{friend-requests.url}}}\n\nTo view {{initiator.name}}''s profile, visit: {{{initiator.url}}}', 'publish', 'closed', 'closed', '', 'site-name-new-friendship-request-from-initiator-name', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=14', 0, 'bp-email', '', 0),
(15, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', '<a href="{{{friendship.url}}}">{{friend.name}}</a> accepted your friend request.', '[{{{site.name}}}] {{friend.name}} accepted your friendship request', '{{friend.name}} accepted your friend request.\n\nTo learn more about them, visit their profile: {{{friendship.url}}}', 'publish', 'closed', 'closed', '', 'site-name-friend-name-accepted-your-friendship-request', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=15', 0, 'bp-email', '', 0),
(16, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', 'Group details for the group &quot;<a href="{{{group.url}}}">{{group.name}}</a>&quot; were updated:\n<blockquote>{{changed_text}}</blockquote>', '[{{{site.name}}}] Group details updated', 'Group details for the group &quot;{{group.name}}&quot; were updated:\n\n{{changed_text}}\n\nTo view the group, visit: {{{group.url}}}', 'publish', 'closed', 'closed', '', 'site-name-group-details-updated', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=16', 0, 'bp-email', '', 0),
(17, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', '<a href="{{{inviter.url}}}">{{inviter.name}}</a> has invited you to join the group: &quot;{{group.name}}&quot;.\n<a href="{{{invites.url}}}">Go here to accept your invitation</a> or <a href="{{{group.url}}}">visit the group</a> to learn more.', '[{{{site.name}}}] You have an invitation to the group: "{{group.name}}"', '{{inviter.name}} has invited you to join the group: &quot;{{group.name}}&quot;.\n\nTo accept your invitation, visit: {{{invites.url}}}\n\nTo learn more about the group, visit {{{group.url}}}.\nTo view {{inviter.name}}''s profile, visit: {{{inviter.url}}}', 'publish', 'closed', 'closed', '', 'site-name-you-have-an-invitation-to-the-group-group-name', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=17', 0, 'bp-email', '', 0),
(18, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', 'You have been promoted to <b>{{promoted_to}}</b> in the group &quot;<a href="{{{group.url}}}">{{group.name}}</a>&quot;.', '[{{{site.name}}}] You have been promoted in the group: "{{group.name}}"', 'You have been promoted to {{promoted_to}} in the group: &quot;{{group.name}}&quot;.\n\nTo visit the group, go to: {{{group.url}}}', 'publish', 'closed', 'closed', '', 'site-name-you-have-been-promoted-in-the-group-group-name', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=18', 0, 'bp-email', '', 0),
(19, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', '<a href="{{{profile.url}}}">{{requesting-user.name}}</a> wants to join the group &quot;{{group.name}}&quot;. As you are an administrator of this group, you must either accept or reject the membership request.\n\n<a href="{{{group-requests.url}}}">Go here to manage this</a> and all other pending requests.', '[{{{site.name}}}] Membership request for group: {{group.name}}', '{{requesting-user.name}} wants to join the group &quot;{{group.name}}&quot;. As you are the administrator of this group, you must either accept or reject the membership request.\n\nTo manage this and all other pending requests, visit: {{{group-requests.url}}}\n\nTo view {{requesting-user.name}}''s profile, visit: {{{profile.url}}}', 'publish', 'closed', 'closed', '', 'site-name-membership-request-for-group-group-name', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=19', 0, 'bp-email', '', 0),
(20, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', '{{sender.name}} sent you a new message: &quot;{{usersubject}}&quot;\n\n<blockquote>&quot;{{usermessage}}&quot;</blockquote>\n\n<a href="{{{message.url}}}">Go to the discussion</a> to reply or catch up on the conversation.', '[{{{site.name}}}] New message from {{sender.name}}', '{{sender.name}} sent you a new message: &quot;{{usersubject}}&quot;\n\n&quot;{{usermessage}}&quot;\n\nGo to the discussion to reply or catch up on the conversation: {{{message.url}}}', 'publish', 'closed', 'closed', '', 'site-name-new-message-from-sender-name', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=20', 0, 'bp-email', '', 0),
(21, 1, '2016-04-28 19:16:53', '2016-04-28 19:16:53', 'You recently changed the email address associated with your account on {{site.name}}. If this is correct, <a href="{{{verify.url}}}">go here to confirm the change</a>.\n\nOtherwise, you can safely ignore and delete this email if you have changed your mind, or if you think you have received this email in error.', '[{{{site.name}}}] Verify your new email address', 'You recently changed the email address associated with your account on {{site.name}}. If this is correct, go to the following link to confirm the change: {{{verify.url}}}\n\nOtherwise, you can safely ignore and delete this email if you have changed your mind, or if you think you have received this email in error.', 'publish', 'closed', 'closed', '', 'site-name-verify-your-new-email-address', '', '', '2016-04-28 19:16:53', '2016-04-28 19:16:53', '', 0, 'http://localhost/ciudadfutura/victoriacolectiva/?post_type=bp-email&p=21', 0, 'bp-email', '', 0),
